const SET_GOAL = "goal/setGoal";

const setGoal = (goal) => ({
    type: SET_GOAL,
    payload: goal,
  });

  export const createGoal = ({
    title,
    description,
    steps,
    deadline,
    userId,
  }) => async (dispatch) => {
    const res = await fetch("/api/goals/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title,
        description,
        steps,
        deadline,
        userId,
      }),
    });
    const data = await res.json();
    dispatch(setGoal(data));
  };
